function add(a, b) {
  return a + b;
}

let sum = add(5, 3); // sum is 8
console.log(sum);

const multiply = (x, y) => x * y;

let product = multiply(4, 6); // product is 24
console.log(product);

function logMessage(message) {
  console.log(message);
}

logMessage("This is a log message.");

let myNumber = 10;
let myFloat = 3.14;
console.log(myNumber, myFloat);

let isTrue = true;
let isFalse = false;
console.log(isTrue, isFalse);

const sym1 = Symbol('foo');
const sym2 = Symbol('foo');
console.log(sym1 === sym2); // false

let globalVariable = 'This is a global variable';

function showGlobal() {
  console.log(globalVariable);  // Accessing the global variable
}

showGlobal();  // Output: This is a global variable
console.log(globalVariable);  // Output: This is a global variable

function myFunction() {
  let functionScoped = 'This is function-scoped';
  console.log(functionScoped);
}

myFunction(); // Output: This is function-scoped
// console.log(functionScoped); // Error: functionScoped is not defined outside of the function.

if (true) {
  let blockScoped = 'This is block-scoped';
  console.log(blockScoped); // Output: This is block-scoped
}
// console.log(blockScoped); // Error: blockScoped is not defined outside of the block

function testVar() {
  if (true) {
    var varScoped = 'I am var scoped';
    console.log(varScoped); // Output: I am var scoped
  }
  console.log(varScoped); // Output: I am var scoped (because var is function-scoped)
}

testVar();

let p = true;
let q = false;

console.log(p && q); // false (AND)
console.log(p || q); // true (OR)
console.log(!p);   // false (NOT)
console.log(!q);   // true (NOT)
