//[[src/i18n.js]]
import i18next from 'i18next';
import { initReactI18next } from 'react-i18next';
import LanguageDetector from 'i18next-browser-languagedetector';
import enTranslation from '../locales/en/translation.json';
import frTranslation from '../locales/fr/translation.json';

i18next
  .use(LanguageDetector)
  .use(initReactI18next)
  .init({
    resources: {
      en: {
        translation: enTranslation,
      },
      fr: {
        translation: frTranslation,
      },
    },
    fallbackLng: 'en', // default language
    interpolation: {
      escapeValue: false, // not needed for react as it escapes by default
    },
    detection: {
      order: ['localStorage', 'navigator'], // order of detection methods
      lookupLocalStorage: 'i18nextLng',   // key for storing language in localStorage
      caches: ['localStorage'],
    },
  });

export default i18next;

//[[src/main.jsx]]
import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';
import { I18nextProvider } from 'react-i18next';
import i18n from './i18n';

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <I18nextProvider i18n={i18n}>
      <App />
    </I18nextProvider>
  </React.StrictMode>
);

//[[src/App.jsx]]
import React from 'react';
import { useTranslation } from 'react-i18next';

function App() {
  const { t } = useTranslation();

  return (
    <div className="App">
      <h1>{t('greeting')}</h1>
      <p>{t('welcome', { name: 'User' })}</p>
      <p>{t('count_apples_plural', { count: 2 })}</p>
    </div>
  );
}

export default App;

//[[src/LanguageSwitcher.jsx]]
import React from 'react';
import { useTranslation } from 'react-i18next';

function LanguageSwitcher() {
  const { i18n } = useTranslation();

  const changeLanguage = (lng) => {
    i18n.changeLanguage(lng);
  };

  return (
    <div>
      <button onClick={() => changeLanguage('en')}>English</button>
      <button onClick={() => changeLanguage('fr')}>French</button>
    </div>
  );
}

export default LanguageSwitcher;

//[[src/App.jsx]]
import React from 'react';
import { useTranslation } from 'react-i18next';
import LanguageSwitcher from './LanguageSwitcher';

function App() {
  const { t } = useTranslation();

  return (
    <div className="App">
      <LanguageSwitcher/>
      <h1>{t('greeting')}</h1>
      <p>{t('welcome', { name: 'User' })}</p>
      <p>{t('count_apples_plural', { count: 2 })}</p>
    </div>
  );
}

export default App;
